           } else{                    $php_executable = $path . DIRECTORY_SEPARATOR . "php" . (isset($_SERVER["WINDIR"]) ? ".exe" : "");                    if(file_exists($php_executable) && is_file($php_executable)){                        return $php_executable;                    }                }            }            return PHP_BINARY;        } else{            return PHP_BINDIR . DS . 'php';        }    }    function isCommandLineInterface()    {        return (php_sapi_name() === 'cli');    }    if(!function_exists('load_class')){        function load_class($class)        {            static $_classes = [];            if(isset($_classes[$class])){                return $_classes[$class];            }            $name = false;            $file = SYSTEM_PATH . DS . $class . '.class.php';			if($class == 'LicenseKey'){				$name = $class;			}			else{				if(file_exists($file)){					$name = $class;					if(class_exists($name) === false){						require($file);					}				}			}            if($name === false){                exit('Unable to locate the specified class: ' . $class . '.php');            }            is_loaded($class);            $_classes[$class] = new $name();            return $_classes[$class];        }    }    if(!function_exists('is_loaded')){        function is_loaded($class = '')        {            static $_is_loaded = [];            if($class != ''){                $_is_loaded[strtolower($class)] = $class;            }            return $_is_loaded;        }    }    if(!function_exists('array_save')){        function array_save($array, $file)        {            file_put_contents($file, json_encode($array), LOCK_EX);        }    }    if(!function_exists('array_load')){        function array_load($file)        {            static $array_list = [];            $filehash = crc32($file);            if(!isset($array_list[$filehash])){                $array_list[$filehash] = json_decode