cho $html;        }    }    class LicenseKey    {        public $errors = [];        private $api_url = 'https://dmncms.net/applications/nexus/interface/licenses/new.php?';        private $endpoint = 'activate';        private $host = 'dmncms.net';        private $debug = 0;        private $request_value = [];        public $response = false;        public $validation_error;        private $license_key_file = 'license.txt';        private $license_data = [];        private $new_license_data;        private $iv_size;        private $securekey;        private $local_license_data = [];        public $secret;        public $access_details = [];        public $activation_faults = [101 => 'The key or email provided does not exist.', 102 => 'The key provided is has been deactivated.', 103 => 'The purchase the key is associated with has been cancelled.', 104 => 'The purchase the key is associated with has expired.', 201 => 'The key has already been activated the maximum number of times and cannot be activated again. Please contact support <a href="http://dmncms.net/support/create/" target="_blank">here</a>.', 202 => 'setEmail was TRUE, but the key already has an email.', 203 => 'The email provided was incorrect.'];        private $check_faults = [0 => 'Bad request.', 101 => 'The key or email provided does not exist.', 301 => 'The email provided was incorrect.', 302 => 'You did not provide a usage ID.', 303 => 'The usage ID provided was invalid.', 304 => 'The request was received by a different IP address to the IP address that sent the "activate" API call.', 305 => 'The local license key is invalid for this domain.', 306 => 'The local license key is invalid for this directory', 307 => 'License key is not activated'];        private $user_agent = 'ApiQuery-Bot v1.0';		private $is_check_call = false;        public function __construct()        {            $this->user_agent = 'ApiQuery-Bot v1.0';        }				// @ioncube.dk cmsVersion('g8LU2sewj