 public function validate()        {            $this->check_license_file();            $this->license_data[5] = explode('[DmN]', $this->decrypt());            if(count($this->license_data[5]) > 1){                $this->check_license_data($this->license_data[5][0], $this->license_data[5][1], $this->license_data[5][2]);                if(!$this->check_response()){                    $this->license_data[5][3] = 'INVALID';                    $this->response['license'] = $this->encrypt(implode('[DmN]', $this->license_data[5]));                    $this->response['license'] = substr($this->response['license'], 0, 10) . $this->secret . substr($this->response['license'], 10);                    $this->generate_license_data();                    if($this->write_license() == true){                        $this->validation_error = 'Invalid license';                        writelog($this->validation_error, 'license');                    } else{                        $this->validation_error = $this->errors[0];                        writelog($this->validation_error, 'license');                    }                } else{                    $this->generate_license_data();                    if($this->write_license() == true){                        return true;                    } else{                        $this->validation_error = $this->errors[0];                        writelog($this->validation_error, 'license');                    }                }            }             else{                @unlink(BASEDIR . $this->license_key_file);            }            return false;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function check_response()        {            if($this->response != false){                $this->response = json_decode($this->response, true);                if(is_array($this->response)){                    if(isset($this->response['errorMessage'])){                        $this->validation_error = $this->check_faults[$this->response['errorMessage']];                    } else{                        if($this->response['status'] == 'ACTIVE'){                            if($this->response['uses'] == 0){                                $this->validation_error = 'License key is not activated';                            }                            else{                                return true;                            }                        }                         else{                            if($this->response['status'] == 'INACTIVE'){                                $this->validation_error = $this->activation_faults[103];                            }                            if($this->response['status'] == 'EXPIRED'){                                $this->validation_error = $this->activation_faults[104];                            }                        }                    }                } else{                    $this->validation_error = 'Invalid response received from license server: ' . json_last_error();                    writelog($this->validation_error, 'license');                }            }            return false;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function activation_form()        {            if(isset($_POST['submit_license'])){                $this->validate_form();                if(count($this->errors) == 0){                    $this->access_details();                    $this->activate_license();                    if(!$this->check_activation_response())                        $this->errors[] = $this->validation_error; else{                        $this->add_cron_task('Retry', '*/1 * * * *');                        $this->generate_license_data();                        $this->write_license();                        header('Location: ./index.php');                    }                }            }            echo $this->activation_form_html();            exit;        }				// @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function validate_form()        {            $this->license_data[2] = isset($_POST['license_key']) ? trim($_POST['license_key']) : '';            $this->license_data[3] = isset($_POST['email']) ? trim($_POST['email']) : '';            if($this->license_data[2] == '')                $this->errors[] = 'Please enter license key.';            if($this->license_data[3] == '')                $this->errors[] = 'Please enter email.';            if(!$this->valid_md5_hash($this->license_data[2]))                $this->errors[] = 'Please enter valid license key.';            if(!$this->valid_email($this->license_data[3]))                $this->errors[] = 'Please enter valid email.';            if(in_array($_SERVER['REMOTE_ADDR'], ['127.0.0.1', '::1', 'localhost']))                $this->errors[] = 'You are not allowed to activate license from localhost. Please use valid domain.';        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function check_activation_response()        {            if($this->response != false && $this->response != ''){                $this->response = json_decode($this->response, true);                if(isset($this->response['errorMessage'])){                    $this->validation_error = $this->activation_faults[$this->response['errorMessage']];                    writelog($this->validation_error, 'license');                } else{                    if($this->response['response'] == 'OKAY'){                        return true;                    } else{                        $this->validation_error = 'Invalid response received from license server: ' . json_last_error();                        writelog($this->validation_error, 'license');                    }                }            }            return false;        }        public function generate_license_data()        {            $this->new_license_data = json_encode(['activated' => 1, 'license' => $this->response['license']]);        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function check_license_file()        {            if(!is_writable(BASEDIR . $this->license_key_file)){				writelog('License file not writable '.BASEDIR . $this->license_key_file.'', 'license');                try{                    $this->create_license_file();                    $this->read_license();                    return true;                } catch(Exception $e){                    throw new Exception($e->getMessage());                }            } else{                $this->read_license();            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function create_license_file($return = false)        {            $json = json_encode(['activated' => 0]);            $handle = @fopen(BASEDIR . $this->license_key_file, "w+");            if($handle){                fwrite($handle, $json);                fclose($handle);				chmod(BASEDIR . $this->license_key_file, 0777);                 $this->clear_license_cache();                if($return){                    return $json;                }            } else{                throw new Exception('License: unable to open license storage file ' . $this->license_key_file);            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function read_license()        {            $this->license_data = file(BASEDIR . $this->license_key_file);            if(empty($this->license_data)){				writelog('License data empty '.print_r($this->license_data, true).'', 'license');				//throw new Exception('License data empty '.print_r($this->license_data, true));               $this->license_data[0] = json_decode($this->create_license_file(true), true);            } else{                $this->license_data[0] = json_decode($this->clean_json($this->license_data[0]), true);            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function write_license()        {			if($this->new_license_data != NULL){				$handle = fopen(BASEDIR . $this->license_key_file, "w+");				if($handle != false){					ftruncate($handle, 0);					fwrite($handle, $this->new_license_data);					fclose($handle);					chmod(BASEDIR . $this->license_key_file, 0777); 					$this->clear_license_cache();					return true;				}				else{					writelog('Cannot open '.$this->license_key_file.'', 'license');					return false;				}			}			else{				writelog('New license data is empty.', 'license');				return false;			}        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function clear_license_cache()        {            /*$cache_files = [                BASEDIR . 'application' . DS . 'data' . DS . 'cache' . DS . 'license_information.dmn',                BASEDIR . 'application' . DS . 'data' . DS . 'cache' . DS . 'available_upgrades.dmn'            ];            foreach($cache_files AS $file){                if(file_exists($file)){                    unlink($file);                }            }*/        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function get_cms_version()        {            if(is_readable($path = BASEDIR . 'application' . DS . 'config' . DS . 'cms_config.json')){				return json_decode(file_get_contents($path), true)['version'];			}			return '';        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function activate_license()        {            $this->check_dmn_host();            $this->endpoint = 'activate';            $this->request_value = ['key' => $this->license_data[2], 'identifier' => $this->license_data[3], 'domain' => str_replace('www.', '', $this->access_details['domain']), 'dir' => $this->access_details['directory'], 'client_time' => time(), 'extra' => json_encode((object)['domain' => $this->full_domain()])];            $this->send_request();        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function check_license_data($licensekey, $identifier, $usage_id)        {            $this->check_dmn_host();            $this->access_details();            $this->endpoint = 'check';            $this->request_value = ['key' => $licensekey, 'identifier' => $identifier, 'usage_id' => $usage_id, 'domain' => str_replace('www.', '', $this->access_details['domain']), 'dir' => $this->access_details['directory'], 'client_time' => time()];            $this->send_request();        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function update_license_extra($licensekey, $identifier, $usage_id)        {            $this->endpoint = 'updateExtra';            $this->request_value = ['key' => $licensekey, 'identifier' => $identifier, 'usage_id' => $usage_id, 'extra' => json_encode((object)['domain' => $this->full_domain()])];            $this->send_request();            $this->response = false;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function check_local_license($data = false)        {            $this->check_license_file();            if($this->license_data[0]['activated'] == 0){                $this->local_license_data[0] = 'undefined';                $this->local_license_data[1] = 'undefined';                $this->local_license_data[4] = 'undefined';                $this->local_license_data[5] = 'undefined';                $this->local_license_data[6] = 'undefined';                $this->local_license_data[8] = 'License not activated';                $this->local_license_data[9] = 'undefined';                $this->local_license_data[10] = $this->get_cms_version();                $this->local_license_data[11] = 'undefined';                return false;            } else{                $this->local_license_data = explode('[DmN]', $this->decrypt());                if($data == false){                    $this->request_license_data($this->local_license_data[0], $this->local_license_data[1]);                } else{                    $this->response = json_encode($data);                }                if($this->response != false){                    $this->response = json_decode($this->response, true);                    if(isset($this->response['errorMessage'])){                        $this->local_license_data[8] = 'License expired';                        $this->local_license_data[9] = 'undefined';                        $this->local_license_data[11] = seo_string('undefined');                        return false;                    } else{                        $this->local_license_data[8] = ($this->response['expires'] != null) ? $this->response['expires'] : 'LifeTime';                        $this->local_license_data[9] = $this->response['purchase_id'];                        $this->local_license_data[11] = seo_string($this->response['purchase_name']);                    }                    $this->local_license_data[10] = $this->get_cms_version();                } else{                    $this->local_license_data[8] = 'License expired';                    $this->local_license_data[9] = 'undefined';                    $this->local_license_data[11] = seo_string('undefined');                    return false;                }            }            return true;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function check_local_license_for_updates()        {            $this->check_license_file();            if($this->license_data[0]['activated'] == 0){                $this->local_license_data[0] = 'undefined';                $this->local_license_data[1] = 'undefined';                $this->local_license_data[4] = 'undefined';                $this->local_license_data[5] = 'undefined';                $this->local_license_data[6] = 'undefined';                $this->local_license_data[8] = 'License not activated';                $this->local_license_data[9] = 'undefined';                $this->local_license_data[10] = $this->get_cms_version();                $this->local_license_data[11] = 'undefined';                return false;            } else{                $this->local_license_data = explode('[DmN]', $this->decrypt());            }            return true;        }        public function get_local_license_data($data = false)        {            return $this->local_license_data;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function request_license_data($license_key, $identifier)        {            $this->endpoint = 'info';            $this->request_value = ['key' => $license_key, 'identifier' => $identifier];            $this->send_request();        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function release_license()        {            $this->check_local_license();            $this->endpoint = 'release';            $this->request_value = ['key' => $this->local_license_data[0], 'identifier' => $this->local_license_data[1], 'usage_id' => $this->local_license_data[2]];            $this->send_request();            if($this->response != false){                $this->response = json_decode($this->response, true);                if(is_array($this->response)){                    if(isset($this->response['errorMessage'])){                        throw new Exception($this->check_faults[$this->response['errorMessage']]);                    } else{                        if($this->response['status'] == 'RELEASE_S' || $this->response['status'] == 'RELEASE_F'){                            $this->create_license_file($this->local_license_data[10]);                            return true;                        } else{                            if($this->response['status'] == 'INACTIVE'){                                throw new Exception($this->activation_faults[103]);                            }                            if($this->response['status'] == 'EXPIRED'){                                throw new Exception($this->activation_faults[104]);                            }                        }                    }                } else{                    throw new Exception('Invalid response received from license server: ' . json_last_error());                }            }            return false;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function generate_iv($secret = true)        {            if($secret){                $this->set_secret();            }            $this->iv_size = openssl_cipher_iv_length('AES-256-CBC');            $this->securekey = hash('sha256', $this->secret);        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function set_secret()        {            $this->secret = substr($this->license_data[0]['license'], 10, 10);            $this->license_data[0]['license'] = substr_replace($this->license_data[0]['license'], '', 10, 10);        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function encrypt($data)        {            $this->generate_iv(false);            $iv = openssl_random_pseudo_bytes($this->iv_size);            $encrypted = openssl_encrypt($data, 'AES-256-CBC', $this->securekey, OPENSSL_RAW_DATA, $iv);            $encrypted = base64_encode($iv . $encrypted);            return $encrypted;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function decrypt()        {            $this->generate_iv();            $data = base64_decode($this->license_data[0]['license']);            $iv = substr($data, 0, $this->iv_size);            $data = openssl_decrypt(substr($data, $this->iv_size), 'AES-256-CBC', $this->securekey, OPENSSL_RAW_DATA, $iv);            return $data;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function send_request()        {            if(function_exists('curl_init')){                $this->send_curl();            } else{                throw new Exception('Please enable php curl extension.');            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function send_curl()        {            $lock_file_check = APP_PATH . DS . 'logs' . DS . 'lock_license_check.txt';			$lock_file_error = APP_PATH . DS . 'logs' . DS . 'lock_license_error.txt';            if(file_exists($lock_file_error)){                clearstatcache();                $file_time = filemtime($lock_file_error);                $left = ($file_time + (5 * 60)) - time();                if($left > 0){                    throw new Exception('Please wait ' . $left . ' seconds and refresh page.');                } else{                    unlink($lock_file_error);                    $this->create_curl_request();                }            } else{				if(file_exists($lock_file_check)){					clearstatcache();					$file_time = filemtime($lock_file_check);					$left = ($file_time + (3 * 60)) - time();					if($left > 0 && $this->is_check_call){						throw new Exception('Please wait ' . $left . ' seconds and refresh page.');					} else{						unlink($lock_file_check);						$this->create_curl_request();					}				}				else{					$this->create_curl_request();				}            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function create_curl_request()        {            $request = curl_init($this->api_url . $this->endpoint);            if($request == false){                writelog('Unable to initialize curl', 'license');                return false;            }            curl_setopt($request, CURLOPT_POST, 1);            curl_setopt($request, CURLOPT_RETURNTRANSFER, 1);            curl_setopt($request, CURLOPT_POSTFIELDS, $this->request_value);            curl_setopt($request, CURLOPT_FORBID_REUSE, 0);            if($this->debug == 1){                curl_setopt($request, CURLOPT_HEADER, 1);                curl_setopt($request, CURLINFO_HEADER_OUT, 1);            }            curl_setopt($request, CURLOPT_CONNECTTIMEOUT, 30);            curl_setopt($request, CURLOPT_HTTPHEADER, ['Connection: Close']);            curl_setopt($request, CURLOPT_USERAGENT, $this->user_agent);            curl_setopt($request, CURLOPT_SSL_VERIFYPEER, false);            if(ini_get('open_basedir') == '' && ini_get('safe_mode') == 'Off'){                curl_setopt($request, CURLOPT_FOLLOWLOCATION, 1);            }            $this->response = curl_exec($request);            if(curl_errno($request) != 0){				file_put_contents(APP_PATH . DS . 'logs' . DS . 'lock_license_error.txt', 1, LOCK_EX);                writelog('Can\'t connect to license server, message:' . curl_error($request), 'license');                throw new Exception('Unable to contact license server, please try again later.');            } else{                if($this->debug == 1){                    writelog('HTTP request of validation request:' . curl_getinfo($request, CURLINFO_HEADER_OUT), 'license');                    writelog('HTTP response of validation request: ' . $this->response, 'license');                    $this->response = substr($this->response, curl_getinfo($request, CURLINFO_HEADER_SIZE));                }                /*if(in_array($this->endpoint, array('activate', 'check'))){                    $response = json_decode($this->response, true);                    if(!isset($response['errorCode'])){                        if(!isset($response['license']) || $response['license'] == ''){                            file_put_contents(APP_PATH . DS . 'logs' . DS . 'lock_license.txt', 1, LOCK_EX);                            writelog('License key was not returned by license server.', 'license');                            throw new Exception('Unable to contact license server, please try again later.');                        }                    }                }*/				if($this->is_check_call){					file_put_contents(APP_PATH . DS . 'logs' . DS . 'lock_license_check.txt', 1, LOCK_EX);				}                curl_close($request);                return true;            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function clean_json($data)        {            preg_match('/{(.*?)}/', $data, $matches);            if(isset($matches[0]))                return $matches[0];            return $data;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function valid_md5_hash($md5 = '')        {            return strlen($md5) == 32 && ctype_xdigit($md5);        }        private function valid_email($email = '')        {            return filter_var($email, FILTER_VALIDATE_EMAIL);        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function full_domain()        {            if(array_key_exists('HTTPS', $_SERVER) && ($_SERVER['HTTPS'] == 'on'))                $url = 'https://'; else                $url = 'http://';            $url .= $_SERVER['SERVER_NAME'] . $_SERVER['SCRIPT_NAME'];            $parts = parse_url($url);            if(substr($parts['path'], -1, 1) == '/')                $parts['dirpath'] = $parts['path']; else                $parts['dirpath'] = substr($parts['path'], 0, strrpos($parts['path'], '/') + 1);            if((int)$_SERVER['SERVER_PORT'] <> 80 && (int)$_SERVER['SERVER_PORT'] <> 443)                $full_domain = $parts['scheme'] . '://' . $parts['host'] . ':' . $_SERVER['SERVER_PORT'] . $parts['dirpath']; else                $full_domain = $parts['scheme'] . '://' . $parts['host'] . $parts['dirpath'];            unset($url);            unset($parts);            return $full_domain;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function access_details()        {            if(function_exists('phpinfo')){                ob_start();                phpinfo();                $phpinfo = ob_get_contents();                ob_end_clean();                $list = strip_tags($phpinfo);                $this->access_details['domain'] = $this->scrape_phpinfo($list, 'HTTP_HOST');            }            $this->access_details['directory'] = BASEDIR;            $this->access_details['domain'] = ($this->access_details['domain']) ? $this->access_details['domain'] : $_SERVER['HTTP_HOST'];            foreach($this->access_details as $key => $value){                $this->access_details[$key] = ($this->access_details[$key]) ? $this->access_details[$key] : 'Unknown';            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        public function check_dmn_host()        {            $info = dns_get_record('dmncms.net', DNS_A);            if($info[0]['ip'] != '185.30.165.29'){                throw new Exception('Not allowed.');            }        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function scrape_phpinfo($all, $target)        {            $all = explode($target, $all);            if(count($all) < 2){                return false;            }            $all = explode("\n", $all[1]);            $all = trim($all[0]);            if($target == 'System'){                $all = explode(" ", $all);                $all = trim($all[(strtolower($all[0]) == 'windows' && strtolower($all[1]) == 'nt') ? 2 : 1]);            }            if($target == 'SCRIPT_FILENAME'){                $all = explode(DS, $all);                array_pop($all);                $all = implode(DS, $all);            }            if(substr($all, 1, 1) == ']'){                return false;            }            return $all;        }                // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        private function add_cron_task($task, $time)        {            $file = BASEDIR . 'application' . DS . 'config' . DS . 'scheduler_config.json';            $data = file_get_contents($file);            $tasks = json_decode($data, true);            if(!array_key_exists($task, $tasks['tasks'])){                $tasks['tasks'][$task] = [					'time' => $time,					'status' => 1,					'desc' => ''				];            }            file_put_contents($file, json_encode($tasks));        }        public function activation_form_html()        {            $html = '<!DOCTYPE html>			<html>			<head>				<meta charset="utf-8">				<title>DmN MuCMS - License Activation</title>				<style>:focus{outline:none}body{font:14px/21px "Lucida Sans","Lucida Grande","Lucida Sans Unicode",sans-serif;margin:0;padding:0}a{color:#68b12f}.main{margin:0 auto;text-align:left;width:800px}.license_form h2,.order_form label{font-family:Georgia,Times,"Times New Roman",serif}.form_hint,.required_notification{font-size:11px}.license_form ul{width:750px;list-style-type:none;list-style-position:outside;margin:0;padding:0}.license_form li{padding:12px;border-bottom:1px solid #eee;position:relative}.license_form li:first-child{border-bottom:1px solid #777}.license_form h2{margin:0;display:inline}.license_form label{width:150px;margin-top:3px;display:inline-block;float:left;padding:3px}.license_form input{height:20px;width:220px;padding:5px 8px}.license_form textarea{padding:8px;width:300px}.license_form button{margin-left:156px}.license_form input,.license_form textarea{border:1px solid #aaa;box-shadow:0 0 3px #ccc,0 10px 15px #eee inset;border-radius:2px;padding-right:30px;-moz-transition:padding .25s;-webkit-transition:padding .25s;-o-transition:padding .25s;transition:padding .25s}.license_form input:focus,.license_form textarea:focus{background:#fff;border:1px solid #555;box-shadow:0 0 3px #aaa;padding-right:70px}.license_form input:required,.license_form textarea:required{background:#fff url(data:image/gif;base64,) no-repeat 98% center}.license_form input:required:valid,.license_form textarea:required:valid{background:#fff url(data:image/gif;base64,) no-repeat 98% center;box-shadow:0 0 5px #5cd053;b